import React from 'react';
import { View, StyleSheet, SectionList, Text } from 'react-native';
import { Card, Title, Chip } from 'react-native-paper';

interface AdminUsuariosScreenProps {
    navigation: any;
}

const AdminUsuariosScreen: React.FC<AdminUsuariosScreenProps> = ({ navigation }) => {
    const usuarios = [
        {
            id: 1,
            nome: 'Usuário Admin',
            username: '123',
            password: '123',
            perfil: 'gerente',
            ativo: true,
            created_at: new Date().toISOString()
        }
    ];

    return (
        <View style={styles.container}>
            <View style={styles.header}>
                <Title style={styles.title}>Administração de Usuários</Title>
                <Text style={styles.subtitle}>Lista de usuários do sistema</Text>
            </View>

            <SectionList
                sections={[{ title: 'Usuários', data: usuarios }]}
                keyExtractor={(item) => item.id.toString()}
                renderItem={({ item }) => <Text>{item.nome}</Text>}
            />
        </View>
    );
};

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: '#f5f5f5',
    },
    header: {
        padding: 20,
        backgroundColor: '#fff',
        borderBottomWidth: 1,
        borderBottomColor: '#e0e0e0',
    },
    title: {
        fontSize: 24,
        fontWeight: 'bold',
        color: '#333',
        marginBottom: 5,
    },
    subtitle: {
        fontSize: 16,
        color: '#666',
    },
    listContainer: {
        padding: 20,
    },
    userCard: {
        marginBottom: 15,
        elevation: 2,
    },
    userHeader: {
        flexDirection: 'row',
        justifyContent: 'space-between',
        alignItems: 'center',
        marginBottom: 15,
    },
    userName: {
        fontSize: 18,
        fontWeight: 'bold',
        color: '#333',
        flex: 1,
    },
    statusChip: {
        borderRadius: 20,
    },
    activeChip: {
        backgroundColor: '#e8f5e8',
        borderColor: '#4caf50',
    },
    inactiveChip: {
        backgroundColor: '#ffebee',
        borderColor: '#f44336',
    },
    activeText: {
        color: '#4caf50',
    },
    inactiveText: {
        color: '#f44336',
    },
    userDetails: {
        backgroundColor: '#f9f9f9',
        padding: 15,
        borderRadius: 8,
    },
    detailRow: {
        flexDirection: 'row',
        justifyContent: 'space-between',
        alignItems: 'center',
        marginBottom: 10,
    },
    detailLabel: {
        fontSize: 16,
        fontWeight: 'bold',
        color: '#555',
        flex: 1,
    },
    detailValue: {
        fontSize: 16,
        color: '#333',
        flex: 2,
        textAlign: 'right',
    },
});

export default AdminUsuariosScreen;
