import React, { useState, useEffect } from 'react';
import {
    View,
    StyleSheet,
    ScrollView,
    Alert,
    TouchableOpacity,
    Modal,
    TextInput,
    Switch,
    ActivityIndicator
} from 'react-native';
import { Card, Title, Paragraph, Button, FAB, Text, Chip, Divider } from 'react-native-paper';
import AsyncStorage from '@react-native-async-storage/async-storage';

interface AdminUsuariosScreenProps {
    navigation: any;
}

interface Usuario {
    id: number;
    nome: string;
    username: string;
    perfil: string;
    ativo: boolean;
    created_at: string;
}

const AdminUsuariosScreen: React.FC<AdminUsuariosScreenProps> = ({ navigation }) => {
    const [usuarios, setUsuarios] = useState<Usuario[]>([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const [modalVisible, setModalVisible] = useState(false);
    const [usuarioEditando, setUsuarioEditando] = useState<Usuario | null>(null);
    const [formData, setFormData] = useState({
        nome: '',
        username: '',
        password: '',
        perfil: 'funcionario',
        ativo: true
    });

    useEffect(() => {
        carregarUsuarios();
    }, []);

    const carregarUsuarios = async () => {
        try {
            const userData = await AsyncStorage.getItem('user');
            if (!userData) {
                navigation.replace('Login');
                return;
            }

            const user = JSON.parse(userData);
            if (user.perfil !== 'gerente') {
                Alert.alert('Acesso negado', 'Apenas gerentes podem acessar esta tela');
                navigation.goBack();
                return;
            }

            const response = await fetch('http://10.56.218.93:5000/usuarios');
            const data = await response.json();
            setUsuarios(data);
        } catch (error) {
            setError('Erro ao carregar usuários');
        } finally {
            setLoading(false);
        }
    };

    const abrirModal = (usuario?: Usuario) => {
        if (usuario) {
            setUsuarioEditando(usuario);
            setFormData({
                nome: usuario.nome,
                username: usuario.username,
                password: '',
                perfil: usuario.perfil,
                ativo: usuario.ativo
            });
        } else {
            setUsuarioEditando(null);
            setFormData({
                nome: '',
                username: '',
                password: '',
                perfil: 'funcionario',
                ativo: true
            });
        }
        setModalVisible(true);
        setError('');
    };

    const fecharModal = () => {
        setModalVisible(false);
        setUsuarioEditando(null);
        setFormData({
            nome: '',
            username: '',
            password: '',
            perfil: 'funcionario',
            ativo: true
        });
        setError('');
    };

    const handleSubmit = async () => {
        if (!formData.nome.trim() || !formData.username.trim() || (!usuarioEditando && !formData.password.trim())) {
            setError('Preencha todos os campos obrigatórios');
            return;
        }

        setLoading(true);
        setError('');

        try {
            const url = usuarioEditando
                ? `http://10.56.218.93:5000/usuarios/${usuarioEditando.id}`
                : 'http://10.56.218.93:5000/usuarios';

            const method = usuarioEditando ? 'PUT' : 'POST';

            const dadosEnvio: any = { ...formData };

            if (usuarioEditando && !dadosEnvio.password) {
                delete dadosEnvio.password;
            }

            const response = await fetch(url, {
                method: method,
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(dadosEnvio)
            });

            const data = await response.json();

            if (data.success) {
                await carregarUsuarios();
                fecharModal();
            } else {
                setError(data.message);
            }
        } catch (error) {
            setError('Erro de conexão com o servidor');
        } finally {
            setLoading(false);
        }
    };

    const excluirUsuario = async (usuario: Usuario) => {
        Alert.alert(
            'Confirmar exclusão',
            `Tem certeza que deseja excluir o usuário "${usuario.nome}"?`,
            [
                { text: 'Cancelar', style: 'cancel' },
                {
                    text: 'Excluir',
                    style: 'destructive',
                    onPress: async () => {
                        try {
                            const response = await fetch(`http://10.56.218.93:5000/usuarios/${usuario.id}`, {
                                method: 'DELETE'
                            });

                            const data = await response.json();

                            if (data.success) {
                                await carregarUsuarios();
                            } else {
                                setError(data.message);
                            }
                        } catch (error) {
                            setError('Erro ao excluir usuário');
                        }
                    }
                }
            ]
        );
    };

    const alternarStatus = async (usuario: Usuario) => {
        try {
            const response = await fetch(`http://10.56.218.93:5000/usuarios/${usuario.id}`, {
                method: 'PUT',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({
                    ativo: !usuario.ativo
                })
            });

            const data = await response.json();

            if (data.success) {
                await carregarUsuarios();
            } else {
                setError(data.message);
            }
        } catch (error) {
            setError('Erro ao alterar status do usuário');
        }
    };

    if (loading) {
        return (
            <View style={styles.loadingContainer}>
                <ActivityIndicator size="large" color="#007bff" />
                <Text style={styles.loadingText}>Carregando usuários...</Text>
            </View>
        );
    }

    return (
        <View style={styles.container}>
            <ScrollView>
                {/* Header */}
                <View style={styles.header}>
                    <Title style={styles.title}>Administração de Usuários</Title>
                    <Paragraph style={styles.subtitle}>Gerencie usuários do sistema</Paragraph>
                </View>

                {/* Error message */}
                {error ? (
                    <Card style={styles.errorCard}>
                        <Card.Content>
                            <Text style={styles.errorText}>{error}</Text>
                        </Card.Content>
                    </Card>
                ) : null}

                {/* Users list */}
                {usuarios.map((usuario) => (
                    <Card key={usuario.id} style={styles.userCard}>
                        <Card.Content>
                            <View style={styles.userHeader}>
                                <View style={styles.userInfo}>
                                    <Title style={styles.userName}>{usuario.nome}</Title>
                                    <Paragraph style={styles.userUsername}>@{usuario.username}</Paragraph>
                                </View>
                                <Chip
                                    mode="outlined"
                                    style={[
                                        styles.roleChip,
                                        usuario.perfil === 'gerente' ? styles.managerChip : styles.employeeChip
                                    ]}
                                >
                                    {usuario.perfil}
                                </Chip>
                            </View>

                            <View style={styles.userDetails}>
                                <View style={styles.statusContainer}>
                                    <Text style={styles.statusLabel}>Status:</Text>
                                    <Chip
                                        mode="flat"
                                        style={usuario.ativo ? styles.activeChip : styles.inactiveChip}
                                    >
                                        {usuario.ativo ? 'Ativo' : 'Inativo'}
                                    </Chip>
                                </View>
                                <Text style={styles.createdAt}>
                                    Criado em: {new Date(usuario.created_at).toLocaleDateString('pt-BR')}
                                </Text>
                            </View>

                            <Divider style={styles.divider} />

                            <View style={styles.actionsContainer}>
                                <Button
                                    mode="outlined"
                                    onPress={() => abrirModal(usuario)}
                                    style={styles.actionButton}
                                >
                                    Editar
                                </Button>
                                <Button
                                    mode="outlined"
                                    onPress={() => alternarStatus(usuario)}
                                    style={[
                                        styles.actionButton,
                                        usuario.ativo ? styles.deactivateButton : styles.activateButton
                                    ]}
                                >
                                    {usuario.ativo ? 'Desativar' : 'Ativar'}
                                </Button>
                                <Button
                                    mode="outlined"
                                    onPress={() => excluirUsuario(usuario)}
                                    style={styles.deleteButton}
                                >
                                    Excluir
                                </Button>
                            </View>
                        </Card.Content>
                    </Card>
                ))}
            </ScrollView>

            {/* FAB for adding new user */}
            <FAB
                icon="plus"
                onPress={() => abrirModal()}
                style={styles.fab}
            />

            {/* Modal for add/edit user */}
            <Modal
                animationType="slide"
                transparent={true}
                visible={modalVisible}
                onRequestClose={fecharModal}
            >
                <View style={styles.modalOverlay}>
                    <View style={styles.modalContent}>
                        <ScrollView>
                            <Title style={styles.modalTitle}>
                                {usuarioEditando ? 'Editar Usuário' : 'Novo Usuário'}
                            </Title>

                            {/* Name */}
                            <TextInput
                                style={styles.input}
                                placeholder="Nome completo"
                                value={formData.nome}
                                onChangeText={(text) => setFormData({ ...formData, nome: text })}
                            />

                            {/* Username */}
                            <TextInput
                                style={styles.input}
                                placeholder="Username"
                                value={formData.username}
                                onChangeText={(text) => setFormData({ ...formData, username: text })}
                                autoCapitalize="none"
                            />

                            {/* Password */}
                            <TextInput
                                style={styles.input}
                                placeholder="Senha"
                                value={formData.password}
                                onChangeText={(text) => setFormData({ ...formData, password: text })}
                                secureTextEntry
                            />

                            {/* Role */}
                            <View style={styles.roleContainer}>
                                <Text style={styles.roleLabel}>Perfil:</Text>
                                <View style={styles.roleButtons}>
                                    <TouchableOpacity
                                        style={[
                                            styles.roleButton,
                                            formData.perfil === 'funcionario' && styles.roleButtonActive
                                        ]}
                                        onPress={() => setFormData({ ...formData, perfil: 'funcionario' })}
                                    >
                                        <Text style={[
                                            styles.roleButtonText,
                                            formData.perfil === 'funcionario' && styles.roleButtonTextActive
                                        ]}>
                                            Funcionário
                                        </Text>
                                    </TouchableOpacity>
                                    <TouchableOpacity
                                        style={[
                                            styles.roleButton,
                                            formData.perfil === 'gerente' && styles.roleButtonActive
                                        ]}
                                        onPress={() => setFormData({ ...formData, perfil: 'gerente' })}
                                    >
                                        <Text style={[
                                            styles.roleButtonText,
                                            formData.perfil === 'gerente' && styles.roleButtonTextActive
                                        ]}>
                                            Gerente
                                        </Text>
                                    </TouchableOpacity>
                                </View>
                            </View>

                            {/* Active status (only for edit) */}
                            {usuarioEditando && (
                                <View style={styles.switchContainer}>
                                    <Text style={styles.switchLabel}>Usuário ativo:</Text>
                                    <Switch
                                        value={formData.ativo}
                                        onValueChange={(value) => setFormData({ ...formData, ativo: value })}
                                    />
                                </View>
                            )}

                            {/* Error message */}
                            {error ? (
                                <Text style={styles.modalErrorText}>{error}</Text>
                            ) : null}

                            {/* Buttons */}
                            <View style={styles.modalButtons}>
                                <Button
                                    mode="outlined"
                                    onPress={fecharModal}
                                    style={styles.modalButton}
                                >
                                    Cancelar
                                </Button>
                                <Button
                                    mode="contained"
                                    onPress={handleSubmit}
                                    loading={loading}
                                    disabled={loading}
                                    style={styles.modalButton}
                                >
                                    {usuarioEditando ? 'Atualizar' : 'Criar'}
                                </Button>
                            </View>
                        </ScrollView>
                    </View>
                </View>
            </Modal>
        </View>
    );
};

